using UnityEngine; using UnityEngine.UI; using System;  public class 收杆 : MonoBehaviour {  	public GameObject btnObj; 	public GameObject yugan; 	public GameObject yu1; 	public GameObject yuwenzi1; 	public GameObject yu2; 	public GameObject yuwenzi2; 	public GameObject shibai; 	public Sprite   expan; 	public Sprite  back; 	Button btn; 	bool isshow=false ;  	// Use this for initialization 	void Start () { 		 		yu1.SetActive (isshow); 		yu2.SetActive (isshow); 		yuwenzi1.SetActive (isshow); 		yuwenzi2.SetActive (isshow); 		shibai.SetActive (isshow);  		btn = btnObj.GetComponent<Button>(); 		btn.onClick.AddListener(delegate () 			{ 				isshow=!isshow; 				if (isshow) 				{ 					int pr = UnityEngine.Random.Range(0, 101); 					if(pr<5){ 						shibai.SetActive (isshow); 					} 					else if(pr<30){ 						yu2.SetActive (isshow); 						yuwenzi2.SetActive (isshow); 					} 					else{ 						yu1.SetActive (isshow); 						yuwenzi1.SetActive (isshow); 					} 					btn.GetComponent<Image>().sprite=expan ; 					yugan.transform.localEulerAngles = new Vector3 (0.0f, 0.0f, 0.0f); 					yugan.transform.localPosition = new Vector3(3.3f,0.0f,5f); 				} 				else { 					btn.GetComponent<Image>().sprite=back; 				} 			} );  	}  	// Update is called once per frame 	void Update () {  	} }